    import E from 'wangeditor';
import VDistpicker from 'v-distpicker';
export default {
    name: 'editorElem',
    data() {
        return {
            editor: null,
            editorContent: '',
            text: '姓名<span class="com-text">[]</span> 性别<span class="com-radio">[]</span> 出生日期<span class="com-date">[]</span> 写表时间<span class="com-time">[]</span> 婚史<span class="com-select">[]</span>'
        }
    },
    components: {VDistpicker},
    // props: ['catchData', 'content'], // 接收父组件的方法
    watch: {
        content() {
            this.editor.txt.html(this.content)
        }
    },
    methods: {
        cd(e) {
            switch (e.target.className) {
                case 'com-text':
                    e.innerHTML = this.getText();
                    break;

                case 'com-radio':
                    e.innerHTML = this.getRadio();
                    break;

                case 'com-date':
                    e.innerHTML = this.getDate();
                    break;

                case 'com-time':
                    e.innerHTML = this.getTime();
                    break;

                case 'com-select':
                    e.innerHTML = this.getSelect();
                    break;
            }
        },
        getText() {
            this.$prompt('请输入姓名', '', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
            }).then(({ value }) => {
                return value
            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: '取消输入'
                });
            });
        },
        getRadio() {
            const h = this.$createElement;
            this.$alert('<input type="radio" value="1" /> 男 <input type="radio" value="2" /> 女', '请选择性别', {
                dangerouslyUseHTMLString: true
            });
        },
        getDate(){
            this.$refs['date_select'].focus()
        },
        getTime(){
            this.$refs['time_select'].focus()
        }

    },
    mounted() {
        this.editor = new E(this.$refs.editorElem)
        this.editor.customConfig.onchange = (html) => {
            this.editorContent = html
            // this.catchData(this.editorContent) // 把这个html通过catchData的方法传入父组件
        }
        this.editor.customConfig.uploadImgServer = '你的上传图片的接口'
        this.editor.customConfig.uploadFileName = '你自定义的文件名'
        this.editor.customConfig.menus = [ // 菜单配置
            'head', // 标题
            'bold', // 粗体
            'fontSize', // 字号
            'fontName', // 字体
            'italic', // 斜体
            'underline', // 下划线
            'strikeThrough', // 删除线
            'foreColor', // 文字颜色
            'backColor', // 背景颜色
            'link', // 插入链接
            'list', // 列表
            'justify', // 对齐方式
            'quote', // 引用
            'emoticon', // 表情
            // 'image', // 插入图片
            'table', // 表格
            'code', // 插入代码
            'undo', // 撤销
            'redo' // 重复
        ]


        this.editor.create() // 创建富文本实例
        if (!this.content) {
            this.editor.txt.html(this.text)
        }
    }
}
